@bot.command()
async def help(ctx, page=None):
	# Custom help command.  Each main category is set as a 'page'.
	if page == 'ctf':
		emb = discord.Embed(description=help_info.ctf_help, colour=4387968)
		emb.set_author(name='CTF Help')
	elif page == 'pwn':
		emb = discord.Embed(description=help_info.pwn_help, colour=4387968)
		emb.set_author(name='Pwn Help')
	elif page == 'ctfd':
		emb = discord.Embed(description=help_info.ctfd_help, colour=4387968)
		emb.set_author(name='CTFd Help')
	elif page == 'web':
		emb = discord.Embed(description=help_info.web_help, colour=4387968)
		emb.set_author(name='Web Help')
	else:
		emb = discord.Embed(description=help_info.help_page, colour=4387968)
		emb.set_author(name='x3rio Help')

	await ctx.channel.send(embed=emb)


@bot.event
async def on_command_error(ctx, error):
	if isinstance(error, commands.CommandNotFound):
		return
	if isinstance(error, commands.MissingRequiredArgument):
		await ctx.send("Missing a required argument.  Do !help")
	if isinstance(error, commands.MissingPermissions):
		await ctx.send("You do not have the appropriate permissions to run this command.")
	if isinstance(error, commands.BotMissingPermissions):
		await ctx.send("I don't have sufficient permissions!")
	else:
		print("error not caught")
		print(error)

@bot.command(name="music")
async def music(ctx, link):
	print("in music")
	if "m.youtube" in link:
		link = link.replace("m.youtube", "www.youtube")
	s = requests.Session()
	link = link.replace("youtube", "320youtube")
	r=s.get(link).text
	url = re.findall(r'action=[\'"]?([^\'" >]+)', r)
	await ctx.channel.send(url)
	print("done music")

@bot.command(name = "ask")
async def how(ctx, *question):
	print("inside howdoi")
	question = ' '.join(question)
	print(question)
	if "--all" in question:
		result = subprocess.check_output(['howdoi', question, '--all'])
	else:
		result = subprocess.check_output(['howdoi', question])
	result = result.decode('utf-8')
	embed = discord.Embed(title=f"{question}", description=f"```{result}```", color = 0x00feca)
	await ctx.channel.send(embed=embed)


